import { LitElement, html, css } from 'lit';


const logo = new URL('../assets/open-wc-logo.svg', import.meta.url).href;

class MyCard extends LitElement {
  static properties = {
    header: { type: String },
  }

  static styles = css`
  img {
    width: 200px;
    height: 200px;
    background-color: orange;
  }
  .card {
    max-width: 400px;
    margin: 0 auto;
    border: 1px solid #ccc;
    padding: 16px;
    display: flex;
    background-color: lightyellow;
    flex-direction: column;
    text-align: center;
  }
  
  .card img {
    max-width: 100%;
    height: auto;
  }
  
  .card-content {
    flex: 1;
  }
  
  h2 {
    font-family: "Verdana", sans-serif;
    font-size: 24px;
    margin: 8px 0;
  }
  
  p.description {
    font-family: "Verdana", serif;
    font-size: 16px;
    margin: 8px 0;
  }
  
  a.details-button {
    background-color: #909cff;
    color: #fff;
    padding: 8px 16px;
    text-decoration: none;
    border-radius: 4px;
  }
  
  @media screen and (max-width: 800px) {
    .details-button {
      display: inline-block;
    }
  }
  
  @media screen and (max-width: 500px) {
    .card {
      max-width: 300px;
    }
    h2 {
      font-size: 20px;
    }
    p.description {
      font-size: 14px;
    }
  }
  
  `;

  constructor() {
    super();
    this.header = 'My app';

  }

  connectedCallback

  firstUpdated(){
    const colorBtn = this.shadowRoot.querySelector('#change-color');
    const titleBtn = this.shadowRoot.querySelector('#heading');
    const cardTitle = this.shadowRoot.querySelector('#cardTitle');
    const deleteBtn = this.shadowRoot.querySelector('#remove');
    const toggleDescription = this.shadowRoot.querySelector('#toggleDescription');
    const card = this.shadowRoot.querySelector('.card-container');
    const cards = this.shadowRoot.querySelector('.card');
    const cardClone = this.shadowRoot.querySelector('.card-container');
    const cardClone2 = this.shadowRoot.querySelector('.card-container');
    const cardDescription = this.shadowRoot.querySelector('#description');
    const btn = this.shadowRoot.querySelector('#btn');
    const cardContent = this.shadowRoot.querySelector('.card-content');
    const cardImage = this.shadowRoot.querySelector('img');
    const summary = this.shadowRoot.querySelector('summary');
    const cardContainer = this.shadowRoot.querySelector('.card-container');

  
    colorBtn.addEventListener('click', this.randomColorGenerator.bind(this));

    titleBtn.addEventListener('click', () => {
      cardTitle.innerHTML = "Something New"
    });

    deleteBtn.addEventListener('click', () => {
      cards.removeChild(cardClone2);
    });

    toggleDescription.addEventListener('click', () => {
      cardDescription.classList.toggle('hidden');
    });

    btn.addEventListener('click', () => {
      cards.appendChild(clone);
    });

    cardContent.addEventListener('click', () => {
      cardImage.classList.toggle('hidden');
    })

    summary.addEventListener('click', () => {
      cardDescription.classList.toggle('hidden');
    })

  }

  randomColorGenerator(){
    const cardContainer = this.shadowRoot.querySelector('.card-container');
    const randomColor = Math.floor(Math.random() * 16777215).toString(16);
    cardContainer.style.backgroundColor = '#' + randomColor;
    return randomColor;
  }

  cloneCard(e) {
    const card = this.shadowRoot.querySelector('.card-container');
    const clone = card.cloneNode(true);
    this.shadowRoot.querySelector('.card').appendChild(clone);
  }

  render() {
    return html`
    <button id="btn" @click="${this.cloneCard}">Copy Card</button>
    <button id="change-color" @click="${this.randomColorGenerator}">Background Color</button>
    <button id="heading">Heading</button>
    <button id="remove">Remove</button>
    <div class="card">
    <div class = "card-container">
      <div class="card-content">
      <h2 id = "cardTitle">Home Real Estate Agency</h2>
    <img src="https://marketplace.canva.com/EAE3lxebXtk/1/0/1600w/canva-home%2C-real-estate%2C-home-design-logo-eaGl0uXNSMo.jpg" alt="Real Estate Logo">
      </div>
      <details>
      <summary id="toggleDescription">About Us</summary>
      <p id= "description"> We provide quality real estate over here at Pod 2 Please Contact us on canvas for further inquires!</p>
        </details>
        </div>
        </div>
    `;
  }
}

customElements.define('my-card', MyCard);